package com.java.practice.functions;

public class DoWhileLoop {  //it is used to executes a block of statements continuously until the given condition is true//

	public static void main(String[] args) {
		int i = 0;
		do {
			System.out.println(i);
			i++;
		}
		while (i<=5);
		// TODO Auto-generated method stub

	}

}
