package com.java.practice.functions;

public class WhileLoop {  //  while loop in java is a control flow statement that executes the code repeatedly based on a given expression//

	public static void main(String[] args) {
		int i =0;
		while (i<=5) {
			System.out.println(i);
			i++;
		}
		
	}
	// TODO Auto-generated method stub

}
